import React, { useEffect, useState } from 'react';
import axios from 'axios';

import "../../Pages/Home/Home.css";
// Assets
import sun from "../../../Assets/Images/Sunny.svg";

const Home = () => {
    const [weatherData, setWeatherData] = useState(null);
    const [input, setInput] = useState(null);

    useEffect(() => {
        axios.get('http://127.0.0.1:8000/get_weather_for_loc/Nadapuram/', {headers: {}}).then((response) => {
            // setWeatherData(response.data)
        })
        navigator.geolocation.getCurrentPosition((position) => {
            const { latitude, longitude } = position.coords;
            console.log('8888888888888888')
            // use the latitude and longitude to get the user's address
            fetch(
              `https://maps.googleapis.com/maps/api/geocode/json?latlng=${latitude},${longitude}&key=YOUR_API_KEY`
            )
              .then((response) => response.json())
              .then((data) => {
                console.log(data)

                if (data.results.length > 0) {
                  console.log(data.results[0].formatted_address);
                }
              });
          });

    },[inSput]);

    const handleInputChange = (inputValue) => {
        console.log(inputValue.target.value)
        setInput(inputValue)



    }

  return (
    <div className='home-page-container'>
        <div className='home-page-subcontainer'>
            <div className='search-section'>
                <input className='search' type="text" placeholder="Search for a city..." onChange={(e) => handleInputChange(e)}>
                </input>
            </div>
            <div className='weather-section-container'>
                <div className='weather-section'>
                    {weatherData !== null ? (
                        <div className='weather-subsection'>
                            <div className='weather-section1'>
                                <div className='temperature-section-container'>
                                    <div className='temperature-section'>
                                        <h1>38</h1>
                                        <h2>o</h2>
                                    </div>
                                    <h1>{weatherData?.weather_desc}</h1>
                                </div>
                                <div className='image-section'>
                                    <img alt='' src={sun}></img>
                                </div>
                            </div>
                            <div className='weather-section2'>
                                {console.log(weatherData)}
                                <h1>Bangalore</h1>
                                <div className='`humidity-section`'>
                                    <h1>Humidity:</h1>
                                <h1>{weatherData?.humidity}</h1>
                                </div>
                            </div>
                        </div>
                    ) : (
                        // {input }
                        <div className='text-section'>Search for a city and view the weather condiion</div>
                    )}
                </div>
            </div>
        </div>
    </div>
  )
}

export default Home
