import React, { useEffect, useState } from 'react';
import axios from 'axios';

import "../../Pages/Home/Home.css";
// Assets
import sun from "../../../Assets/Images/Sunny.svg";

const Home = () => {
    const [weatherData, setWeatherData] = useState();

    useEffect(() => {
        axios.get('http://127.0.0.1:8000/get_weather_for_loc/Nadapuram/', {headers: {}}).then((response) => {
            setWeatherData(response.data)
        })
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
              geoLocationSuccess,
              geoLocationError,
              { timeout: 10000 }
            );
          } else {
            alert("your browser doesn't support geolocation");
          }
          function geoLocationSuccess(pos) {
            // get user lat,long
            var myLat = pos.coords.latitude,
              myLng = pos.coords.longitude,
              loadingTimeout;

            var loading = function () {
            };

            loadingTimeout = setTimeout(loading, 600);

            var request = axios.get(
              "https://nominatim.openstreetmap.org/reverse?format=json&lat=" +
                myLat +
                "&lon=" +
                myLng
            )
              .then(function (data) {
                if (loadingTimeout) {
                  clearTimeout(loadingTimeout);
                  loadingTimeout = null;
                  $locationText.text(data.display_name);
                }
              })
              .fail(function () {
                // handle error
              });
          }

          function geoLocationError(error) {
            var errors = {
              1: "Permission denied",
              2: "Position unavailable",
              3: "Request timeout"
            };
            alert("Error: " + errors[error.code]);
          }


    },[]);

    const handleInputChange = (inputValue) => {
        console.log(inputValue.target.value)



    }

  return (
    <div className='home-page-container'>
        <div className='home-page-subcontainer'>
            <div className='search-section'>
                <input className='search' type="text" placeholder="Search for a city..." onChange={(e) => handleInputChange(e)}>
                </input>
            </div>
            <div className='weather-section-container'>
                <div className='weather-section'>
                    <div className='weather-subsection'>
                        <div className='weather-section1'>
                            <div className='temperature-section-container'>
                                <div className='temperature-section'>
                                    <h1>38</h1>
                                    <h2>o</h2>
                                </div>
                                <h1>{weatherData?.weather_desc}</h1>
                            </div>
                            <div className='image-section'>
                                <img alt='' src={sun}></img>
                            </div>
                        </div>
                        <div className='weather-section2'>
                            {console.log(weatherData)}
                            <h1>Bangalore</h1>
                            <div className='`humidity-section`'>
                                <h1>Humidity:</h1>
                            <h1>{weatherData?.humidity}</h1>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
  )
}

export default Home
